mapper.sql:
where 1=1

	
	    and action_id =  #{actionId}
	
	
	    and tx_id =  #{txId}
	
	
	    and name =  #{name}
	
	
	    and state =  #{state}
	
	
	    and gmt_create =  #{gmtCreate}
	
	
	    and gmt_modified =  #{gmtModified}
	
	
	    and context =  #{context}

mapper.select:
select  from business_action
where 1=1

					
	    and action_id =  #{object.actionId}
	
					
	    and tx_id =  #{object.txId}
	
					
	    and name =  #{object.name}
	
					
	    and state =  #{object.state}
	
					
	    and gmt_create =  #{object.gmtCreate}
	
					
	    and gmt_modified =  #{object.gmtModified}
	
					
	    and context =  #{object.context}
	
	

	order by
	
		${item}

     SELECT
     count(*)
     FROM business_action 
     where 1=1 
     
							
			    and action_id =  #{actionId}
			
							
			    and tx_id =  #{txId}
			
							
			    and name =  #{name}
			
							
			    and state =  #{state}
			
							
			    and gmt_create =  #{gmtCreate}
			
							
			    and gmt_modified =  #{gmtModified}
			
							
			    and context =  #{context}
